/*Dequeue the local copy of jQuery and enqueue Google*/function modify_jquery() { if (!is_admin()) { wp_deregister_script('jquery'); wp_register_script('jquery', 'http://ajax.googleapis.com/ajax/libs/jquery/x.xx.x/jquery.min.js', false, 'x.xx.x'); wp_enqueue_script('jquery'); }}add_action('init', 'modify_jquery');